reactHookExamples.useTopics:
import { useState, useEffect } from 'react';

interface UseTopicsFilters {
  type?: TopicType;
  department?: string;
}

interface UseTopicsReturn {
  topics: Topic[];
  loading: boolean;
  error: string | null;
  pagination: {
    page: number;
    limit: number;
    total: number;
    totalPages: number;
  };
  setPagination: (pagination: { page: number; limit: number }) => void;
  refetch: () => void;
}

const useTopics = (filters: UseTopicsFilters = {}): UseTopicsReturn => {
  const [topics, setTopics] = useState<Topic[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [pagination, setPagination] = useState({
    page: 1,
    limit: 10,
    total: 0,
    totalPages: 0
  });

  const fetchTopics = async () => {
    try {
      setLoading(true);
      setError(null);
      const params = new URLSearchParams({
        page: pagination.page.toString(),
        limit: pagination.limit.toString(),
        ...(filters.type && { type: filters.type }),
        ...(filters.department && { department: filters.department })
      });

      const token = localStorage.getItem('token');
      const response = await fetch(`/api/topics?${params}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (!response.ok) throw new Error('Failed to fetch topics');

      const data: TopicListResponse = await response.json();
      setTopics(data.data);
      setPagination({
        page: data.page,
        limit: data.limit,
        total: data.total,
        totalPages: data.totalPages
      });
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Unknown error');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTopics();
  }, [filters.type, filters.department, pagination.page, pagination.limit]);

  return {
    topics,
    loading,
    error,
    pagination,
    setPagination,
    refetch: fetchTopics
  };
};

reactHookExamples.useCreateTopic:
import { useState } from 'react';

interface UseCreateTopicReturn {
  createTopic: (payload: CreateTopicPayload) => Promise<Topic>;
  loading: boolean;
  error: string | null;
}

const useCreateTopic = (): UseCreateTopicReturn => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const createTopic = async (payload: CreateTopicPayload): Promise<Topic> => {
    try {
      setLoading(true);
      setError(null);

      const token = localStorage.getItem('token');
      const response = await fetch('/api/topics', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify(payload)
      });

      const data: ApiResponse<Topic> = await response.json();

      if (!data.status) throw new Error(data.message);

      return data.data;
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Unknown error';
      setError(errorMessage);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  return { createTopic, loading, error };
};

