reactHookExamples.useSavedTopics:
import { useState, useEffect } from 'react';

interface UseSavedTopicsReturn {
  topics: Topic[];
  loading: boolean;
  error: string | null;
  pagination: {
    page: number;
    limit: number;
    total: number;
    totalPages: number;
  };
  setPagination: (pagination: { page: number; limit: number }) => void;
  refetch: () => void;
}

const useSavedTopics = (): UseSavedTopicsReturn => {
  const [topics, setTopics] = useState<Topic[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [pagination, setPagination] = useState({
    page: 1,
    limit: 10,
    total: 0,
    totalPages: 0
  });

  const fetchSavedTopics = async () => {
    try {
      setLoading(true);
      setError(null);

      const params = new URLSearchParams({
        page: pagination.page.toString(),
        limit: pagination.limit.toString()
      });

      const token = localStorage.getItem('token');
      const response = await fetch(`/api/topics/saved?${params}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (!response.ok) throw new Error('Failed to fetch saved topics');

      const data: TopicListResponse = await response.json();
      setTopics(data.data);
      setPagination({
        page: data.page,
        limit: data.limit,
        total: data.total,
        totalPages: data.totalPages
      });
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Unknown error');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchSavedTopics();
  }, [pagination.page, pagination.limit]);

  return {
    topics,
    loading,
    error,
    pagination,
    setPagination,
    refetch: fetchSavedTopics
  };
};

reactHookExamples.useTopicById:
import { useState, useEffect } from 'react';

interface UseTopicByIdReturn {
  topic: Topic | null;
  loading: boolean;
  error: string | null;
  refetch: () => void;
}

const useTopicById = (id: string): UseTopicByIdReturn => {
  const [topic, setTopic] = useState<Topic | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const fetchTopic = async () => {
    try {
      setLoading(true);
      setError(null);

      const token = localStorage.getItem('token');
      const response = await fetch(`/api/topics/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (!response.ok) throw new Error('Failed to fetch topic');

      const data: ApiResponse<Topic> = await response.json();
      setTopic(data.data);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Unknown error');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (id) fetchTopic();
  }, [id]);

  return { topic, loading, error, refetch: fetchTopic };
};

